Cekgenapganjil &lt;- function(Angka){
  if(Angka %% 2 == 0){
    return(paste(Angka, "Angkanya bertype :Genap"))
  }else{
    return(paste(Angka,"Angkanya bertype : Ganjil"))
  }
}

Cekgenapganjil(5)

